import './Auction.css';
import { Form } from "react-bootstrap";
import { Field, ErrorMessage } from "formik";
import { useState, useEffect } from "react";


function CustomDropdown() {
    const [options, setOptions] = useState([]);
    const results = []
    useEffect(() => {
        async function fetchData() {
            fetch('http://localhost:5001/api/v1/showBids/', {
                method: 'GET',
                headers: {
                    Origin: 'http://localhost:3000/'
                }
            }).then((response) => response.json())
                .then((data) => {
                    data.forEach((value) => {
                        results.push({
                            key: value.productName,
                            value: value.id,
                        });
                    });
                    setOptions([
                        { key: 'Select a product', value: '' },
                        ...results
                    ])
                })
                .catch((err) => {
                    console.log(err.message);
                });
            }
            fetchData();
        }, []);


        return (
            < Form.Group className="mb-2" >
                <Form.Label htmlFor={name}>{label}</Form.Label>
                <Field id={name} name={name} {...rest} as={Form.Select}>
                    {options.map((option) => {
                        return (
                            <option key={option.value} value={option.value}>
                                {option.key}
                            </option>
                        );
                    })}
                </Field>
                <ErrorMessage className="text-danger" name={productName} component={Form.Text} />
            </Form.Group >
        );
    }


        export default CustomDropdown;
