import './Auction.css';
import Product from './Product';
import { useState, useEffect } from "react";
import { CustomDropdown } from './CustomDropdown';


function Auction() {
    const [options, setOptions] = useState([]);
    const results = []
    useEffect(() => {
        fetch('http://localhost:5001/api/v1/showBids/', {
            method: 'GET',
            headers: {
                Origin: 'http://localhost:3000/'
            }
        }).then((response) => response.json())
            .then((data) => {
                data.forEach((value) => {
                    results.push({
                      key: value.productName,
                      value: value.id,
                    });
                });
                setOptions([
                    {key: 'Select a company', value: ''}, 
                    ...results
                ])
            })
            .catch((err) => {
                console.log(err.message);
            });
    }, []);

    return (
        <div>
            <CustomDropdown></CustomDropdown>
            <Product
                productName={products.productName}
                shortDescription={products.shortDescription}
                startPrice={products.startPrice}></Product>
            <div className="App">
                <tbody>
                    <tr>
                        <th>Bid Amount</th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Phone</th>
                    </tr>
                    {bids.map((bid) => {
                        return (
                            <tr>
                                <td>${bid.amount}</td>
                                <td>{bid.firstName}</td>
                                <td>{bid.email}</td>
                                <td>{bid.phone}</td>
                            </tr>
                        );
                    })}
                </tbody>
            </div>
        </div>
    );
}

export default Auction;
