import { useState, useEffect } from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Product(){
    const [posts, setPosts] = useState([]);
    let headers = new Headers();
    headers.append('Origin','http://localhost:3000/');
    useEffect(() => {
        fetch('http://localhost:5001/api/v1/showBids/', {
        method: 'GET',
        headers:headers
        })
        .then((response) => response.json())
        .then((data) => {
           console.log(data.products);
           setPosts(data.products);
        })
        .catch((err) => {
           console.log(err.message);
        });
  }, []);

   return (

    <div className="posts-container">
      {posts.map((post) => {
         return (
           <form>
            <label>
                Product Name
                <input type="text" name="productName" value={post.productName} />
            </label><br></br>
            <label>
                Short Description
                <input type="textarea" name="shortDescription" value={post.shortDescription} />
            </label><br></br>
            <label>
                Detailed Description
                <input type="textarea" name="detailedDescription" value={post.detailedDescription} />
            </label>
           </form>
         );
      })}
   </div>
   );
}

export default Product;
