import './Auction.css';
import Product from './Product';
import { useState, useEffect } from "react";


function Auction() {
    const [posts, setPosts] = useState([]);
    let headers = new Headers();
    headers.append('Origin', 'http://localhost:3001/');
    useEffect(() => {
        fetch('http://localhost:5001/api/v1/showBids/', {
            method: 'GET',
            headers: headers
        })
            .then((response) => response.json())
            .then((data) => {
                setPosts(data.products);
            })
            .catch((err) => {
                console.log(err.message);
            });
    }, []);

    return(
        <div>
            <h2></h2>
        {posts.map((post) => {
            return(
        <div>
        <div className='header'>
            <img src="auction.jpg" alt="Logo" width="200" height="200"/>
            <button type="button" className='button-1'>Fetch Details</button>
        </div>
        <div className='body'>
          <Product 
            productName={post.productName}
            shortDescription={post.shortDescription}
            startPrice={post.startPrice}></Product>
        </div>
        </div>
            );
    })}
    </div>
    );
}

export default Auction;
