import React from 'react';

var values;

class DropDown extends React.Component {

    constructor() {
        super();
        this.state = {
            options: []
        }
    }

    componentDidMount() {
        this.fetchOptions()
    }

    fetchOptions() {
        fetch('http://localhost:5001/api/v1/showBids/', {
            method: 'GET',
            headers: {
                Origin: 'http://localhost:3000/'
            }
        }).then((response) => response.json())
            .then((data) => {
                values = data.products;
                this.setState({ options: values })
                console.log(values);
            })
            .catch((err) => {
                console.log(err.message);
            });
    }
    render() {
        return <div className="drop-down">
            <select>
                {this.state.options.map((productName, startPrice) => <option key={startPrice} value={productName}>{startPrice}</option>)}
            </select>
        </div>;
    }
}

export default DropDown;
