import './Auction.css';
import Product from './Product';
import { useState, useEffect } from "react";


function Auction() {
    const [posts, setPosts] = useState([]);
    let headers = new Headers();
    headers.append('Origin', 'http://localhost:3001/');
    useEffect(() => {
        fetch('http://localhost:5001/api/v1/showBids/', {
            method: 'GET',
            headers: headers
        })
            .then((response) => response.json())
            .then((data) => {
                setPosts(data.products);
                console.log(posts.map((post) =>{
                    return(
                        <h2>{post.productName}</h2>
                    );
            }))
            })
            .catch((err) => {
                console.log(err.message);
            });
    }, []);

    return (
        <div className='header'>
            <img src="auction.jpg" alt="Logo" width="200" height="200" />
            <button type="button" className='button-1'>Fetch Details</button>
        {posts.map((post) => {
            return (
                <Product
                    productName={posts.productName}
                    shortDescription={posts.shortDescription}
                    startPrice={posts.startPrice}></Product>
            );
        })}
    </div>

    );
}

export default Auction;
