import './Auction.css';
import Product from './Product';
import { useState, useEffect } from "react";
import AsyncSelect from 'react-select/async';
import 'bootstrap/dist/css/bootstrap.min.css';


function Auction() {
    const [posts, setPosts] = useState([]);
    useEffect(() => {
        const fetchProducts = () => {
            fetch('http://localhost:5001/api/v1/showBids/', {
                method: 'GET',
                headers: {
                    Origin: 'http://localhost:3001/'
                }
            }).then((response) => response.json())
                .then((data) => {
                    setPosts(data.products);
                })
                .catch((err) => {
                    console.log(err.message);
                });
        };
        fetchProducts();
    }, []);

    return (
        <div>
            {posts.map((post) => {
                return (
                    <div>
                        <div className="container">
                            <div className="row">
                                <div className="col-md-4"> </div>
                            </div>
                            <div className="row">
                                <div className="col-md-4"></div>
                                <div className="col-md-4">
                                    <AsyncSelect
                                        cacheOptions
                                        defaultOptions
                                        value={selectedValue}
                                        getOptionLabel={e => e.first_name + ' ' + e.last_name}
                                        getOptionValue={e => e.id}
                                        loadOptions={fetchUsers}
                                        onInputChange={handleInputChange}
                                        onChange={handleChange}
                                    />
                                </div>
                                <div className="col-md-4"></div>
                            </div>

                            <div className="row userDetails">


                                <div className="col-md-6 img">
                                    <img src={selectedValue && selectedValue.avatar} className="img-rounded" />
                                </div>

                                <div className="col-md-6">
                                    <blockquote >
                                        <h5>{selectedValue && selectedValue.first_name} {selectedValue && selectedValue.last_name}</h5>
                                        <small><cite title="Source Title">  {selectedValue && selectedValue.email}<i className="icon-map-marker"></i></cite></small>
                                    </blockquote>
                                </div>

                            </div>

                        </div>
                        <Product
                            productName={post.productName}
                            shortDescription={post.shortDescription}
                            startPrice={post.startPrice}></Product>
                    </div>
                );
            })}
        </div>

    );
}

export default Auction;
